/*Table structure for table `gb_buy` */

DROP TABLE IF EXISTS `gb_buy`;

CREATE TABLE `gb_buy` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `gbsiteid` int(11) NOT NULL COMMENT '团购站点id',
  `url` varchar(255) NOT NULL COMMENT '网址',
