/*Table structure for table `gb_goods` */

DROP TABLE IF EXISTS `gb_goods`;

CREATE TABLE `gb_goods` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `siteid` int(11) NOT NULL,
  `url` varchar(255) NOT NULL COMMENT '团购地址',
  `title` text NOT NULL COMMENT '标题',
  `simageurl` varchar(255) DEFAULT NULL COMMENT '小图地址',
  `mediumimageur` varchar(255) DEFAULT NULL COMMENT '中图地址',
  `largeimageurl` varchar(255) DEFAULT NULL COMMENT '大图地址',
  `cityid` int(2) DEFAULT NULL COMMENT '城市id',
  `status` int(1) NOT NULL COMMENT '状态',
  `startdate` varchar(30) NOT NULL COMMENT '开始时间',
  `enddate` varchar(30) NOT NULL COMMENT '结束时间',
  `price` varchar(10) NOT NULL COMMENT '团购价格',
  `value` varchar(10) NOT NULL COMMENT '实际价格',
  `amount` int(5) NOT NULL COMMENT '人数',
  `percent` varchar(10) NOT NULL COMMENT '折扣',
  `website` varchar(10) DEFAULT NULL COMMENT '团购名称',
  `expiration` varchar(30) DEFAULT NULL COMMENT '有效期',
  `detail` text COMMENT '描述',
  `createTime` char(20) DEFAULT NULL COMMENT '创建时间',
  `updateTime` char(20) DEFAULT NULL COMMENT '更新时间',
  `isExpired` int(1) DEFAULT '0' COMMENT '是否过期.1:过期,0:未过期',
  `cate` varchar(10) DEFAULT NULL COMMENT '类别',
  `city` varchar(10) DEFAULT NULL COMMENT '城市',
  `siteurl` varchar(100) DEFAULT NULL COMMENT '地区url',
  PRIMARY KEY (`id`),
  UNIQUE KEY `unique_url` (`url`,`siteid`)
) ENGINE=InnoDB DEFAULT CHARSET=gbk;

